sp_widget.client_script:
function($rootScope, $scope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event, data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.noteID = r.data.notes.sys_id;
			c.data.title = r.data.notes.title;
			c.data.note = r.data.notes.note;
		});
	});

	c.updateNote = function(el) {
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			title: c.data.title,
			note: c.data.note
		}).then(function(r) {
			$rootScope.title = c.data.title;
			$rootScope.note = c.data.note;
			$rootScope.$emit('updatedNote');
		});
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.title = 'test';

	if(input && input.noteID) {
		var gs = new GlideRecord('x_snc_createnotes_note');
		if(gs.get(input.noteID)) {
			if(input.action == 'getNote') {
				data.notes = {};
				$sp.getRecordDisplayValues(data.notes, gs, 'sys_id, title, note');
			}

			if(input.action == 'updateNote') {
				gs.title = input.title;
				gs.note = input.note;
				gs.update();
				$sp.getRecordDisplayValues(data.notes, gs, 'sys_id, title, note');
			}
		}
	}
})();
